, номер <?php echo $_smarty_tpl->tpl_vars['data']->value['FlatTypeName'];?>
 <?php echo $_smarty_tpl->tpl_vars['data']->value['RoomViewName'];?>
, &nbsp;розміщення <?php echo $_smarty_tpl->tpl_vars['data']->value['PlacingTypeName'];?>
